function main({ url }) {
  try {
    const parsed = new URL(url);
    return {
      protocol: parsed.protocol,
      hostname: parsed.hostname,
      port: parsed.port || 'default',
      pathname: parsed.pathname,
      search: parsed.search,
      hash: parsed.hash
    };
  } catch (e) {
    return { error: 'Invalid URL' };
  }
}
